<style>.Q0 {background: transparent !important;box-shadow: none !important;-webkit-box-shadow: none !important;-moz-box-shadow: none !important;}.horde, .Q-TOP {display: none;}.MM-1 {margin-top: 30px;}.PRF-11 {padding-top: 0 !important;}</style><?php/** *  * Помошник вида: шапка профиля компании */class Users_View_Helper_ProfileHead extends Zend_View_Helper_Abstract{    public function ProfileHead()    {                        $this->view->headScript()		->appendFile( '/js/users/myfriends.js' );                if ($this->view->isGodOfProject())            $this->view->headScript()		->appendFile( '/js/users/fb_avatars.js' );                $mymessages = '';        $sendmessage = '';        $friend = '';        $user = '';        $edit = '';                $confirmed = '';        $auth = Zend_Auth::getInstance();        if ($auth->hasIdentity()) {                    if ($auth->getIdentity()->id == $this->view->user->id)                $mymessages = '';            if ($auth->getIdentity()->id != $this->view->user->id) {                $this->view->headScript()                    ->appendFile( '/js/users/messages.js' );                                $sendmessage = '<a class="lnone grey" href="' . $this->view->url(array('userId' => $this->view->user->id), 'usermessagesto') . '"><img class="Imensaje" src="/zeta/0.png" width="16" height="16" alt="" /> Enviar mensaje</a>';            }                if ($auth->hasIdentity() && $auth->getIdentity()->id != $this->view->user->id) {                $table = new Users_Model_Table_Friends();                $friendship = $table->checkFriendship($auth->getIdentity()->id, $this->view->user->id, 1);                $friendship_request = $table->checkFriendship($auth->getIdentity()->id, $this->view->user->id, 0);                $r_friendship_request = $table->checkFriendship($this->view->user->id, $auth->getIdentity()->id, 0);                                if (!$friendship && !$friendship_request && !$r_friendship_request && $auth->getIdentity()->id != $this->view->user->id) {                    $friend = '<span id="friend_request" rel="' . $this->view->user->id . '" class="lnone grey"><img class="Ianadir" src="/zeta/0.png" width="16" height="16" alt="" /> Añadir a amigos</span>';                } elseif ($friendship) {                    $friend = '<span id="friend_remove" rel="' . $this->view->user->id . '" class="lnone grey"><img class="Ianadir" src="/zeta/0.png" width="16" height="16" alt="" /> Eliminar de los amigos</span>';                    } elseif ($r_friendship_request) {                    $friend = '<a href="' . $this->view->url(array('requestId' => $r_friendship_request->id), 'friendaccept') . '" class="lnone grey"><img class="Ianadir" src="/zeta/0.png" width="16" height="16" alt="" /> Añadir como amigo</span>';                }                                       if ($friendship_request)                    $friend = '<span id="friend_discard" rel="' . $this->view->user->id . '" class="lnone grey"><img class="Ianadir" src="/zeta/0.png" width="16" height="16" alt="" /> Enviaste una solicitud. ¿Cancelar?</span>';                                } else {                $edit = '<a class="coll editprof" href="' . $this->view->url(array(), "options") . '"></a>';                $user = "user" . $this->view->user->id . "/";            }                        if ($this->view->user->is_confirmed == 0 && $auth->getIdentity()->id == $this->view->user->id) {                $confirmed = '<div class="jcc">                    <strong>¡El usuario esta registrado!</strong><br />                    A su Email hemos enviado las instrucciones para confirmar la cuenta.<br />Por favor, revisa su Email.                </div>';            }                }        $posts = '<a href="' . $this->view->url(array('userId' => $this->view->user->id), 'userposts') . '" class="straight">Publicaciones</a> • ' . count($this->view->user->getPosts(true)) . '&nbsp;&nbsp;';        $events = '<a href="' . $this->view->url(array('userId' => $this->view->user->id), 'userevents') . '" class="straight">Eventos</a> • ' . count($this->view->user->getEvents(true)) . '&nbsp;&nbsp;';        $photoreports = '<a href="' . $this->view->url(array('userId' => $this->view->user->id), 'usergalleries') . '" class="straight">Galerías</a> • ' . count($this->view->user->getPhotoReports());                $facebook = '';        if ($this->view->user->facebook) {            $facebook = 'Facebook: <a class="lhov" target="_blank" href="'. $this->view->user->facebook . '">' . $this->view->user->facebook . '</a><br />';        }                $www = '';        if ($this->view->user->www) {            $www = 'Sitio web: <a class="lhov" href="' . $this->view->user->www . '" target="_blank">' . $this->view->user->www . '</a><br />';        }                $skype = '';        if ($this->view->user->skype) {            $skype = 'Skype: <a class="straight nl" href="skype:' . $this->view->user->skype .'?call">' . $this->view->user->skype .'</a><br />';        }                $twitter = '';        if ($this->view->user->twitter) {            $twitter = 'Twitter: <a class="lhov" target="_blank" href="' . $this->view->user->twitter . '">' . $this->view->user->twitter . '</a>';        }                $fb_avatar = '';        if (!is_null($this->view->user->uid) && $this->view->isGodOfProject()) {            $fb_avatar = '<span id="fb_avatar" rel="' . $this->view->url(array('userId' => $this->view->user->id, 'action' => 'updateavatar'), 'profile') . '">Actualizar avatar</a>';        }                $city = $this->view->user->getCityFromName();        if (!is_null($city)) {            $t_city = $city;        } else {            $t_city = '';        }                $age = $this->view->user->getAge();        $age = !is_null($age) ? ', ' . $age : '';                // <img class="mb10" src="/zeta/zeta364.jpg" width="200" height="200" alt="" />                $output = '<div class="Qblock Qblock5 mb30">                <div class="US3">                        ' . $this->view->user->getAvatar('200', '200', false, 'class="mb10"') . '                        <div class="clear f11">                                ' . $sendmessage . '                                ' . $friend . '                        </div>                </div>                <div class="US4">                        <p class="f20 mb5"><strong>' . $this->view->user->username . '</strong></p>                        <p><strong>' . $this->view->user->firstname . ' ' . $this->view->user->lastname . '</strong></p>                        <p class="grey2 lh17">                                Ranking ' . $this->view->user->rate . '                                <br />                                Con nosotros desde ' . $this->view->dateFormatPostList($this->view->user->signup_date) . '                                <br />                                ' . $this->view->user->getSex() . $age . '                                <br />                                ' . $t_city . '                        </p>                        <p class="grey f10 lh14">' .                                $facebook . $www . $skype . $twitter . $fb_avatar . '                        </p>                </div>        </div>';                return $output;    }}